import React, { Component, useState } from "react";
import { Link } from "react-router-dom";
import '../styles/TelaLogin.css';



function TelaLogin() {

    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");

    const login = () => {
        if (email == 'admin' | !senha) {
          alert("Preencha todos os campos");
          return;
        }

        const res = login(email, senha);

        if (res) {
          alert(res);
          return;
        }

        alert("OK")
    };

    return (
        <section>
            <form className="formLogin">
                <span>
                    <img className="imgLogo" src="http://localhost:3000/ico.png"/>
                </span>
                <h3><b>LOGIN</b></h3>
                <p>Insira seus dados para continuar</p>

                <div className="containerLogin">
                    <p className="title"><b>EMAIL</b></p>
                    <div className="wrapInput">
                        <input 
                            className="email" 
                            type="text" 
                            name="email" 
                            data-testid='email'
                            placeholder="user@exemplo.com.br"/>
                    </div>

                    <p className="title"><b>SENHA</b></p>
                    <div className="wrapInput">
                        <input 
                            className="senha" 
                            type="password" 
                            name="senha" 
                            placeholder="******"/>
                    </div>
                </div>


                <div className="btnLoginContainer">
                    <button onClick={login} className="btnLogin">Entrar</button>
                </div>
            </form>
        </section>

    );

}

export default TelaLogin;
